import React, {Component, useState, useEffect} from 'react';
import Results from './Results.js';


const Search = () => {

	const APP_ID = 'b11d53b9';
	const APP_KEY = '7b14b7d587d0ca76e3e3fa38a302781c';

	const [recipes, setRecipes] = useState([]);


	useEffect( async () =>{
		getRecipes();			
	}, []);

	const getRecipes = async() =>{
		const response = await fetch(`https://protected-waters-53156.herokuapp.com/https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}`);
		const data = await response.json();
		setRecipes(data.hits);
		console.log(data.hits);
	};

		return(
			<div className = "search">
				<form className="search-form">
					<input className = "search-bar" type='text'/>
					<button className = 'search-button' type='submit'>Search</button>
						Search
				</form>
				{Results.map(result => (
					<result />
					))};
			</div>
		);
	}

export default Search;
